templates.template:
${:import(org.seasar.akabana.yui.core.ns.handler,org.seasar.akabana.yui.command.events.CommandEvent)}/**
 * 
 * @param event コマンドの実行結果
 */
handler function ${cmdName:var(org.seasar.akabana.yui.command.core.ICommand)}_complete(event:CommandEvent):void{
    ${cursor}
}

/**
 * 
 * @param event コマンドの実行エラー
 */
handler function ${cmdName}_error(event:CommandEvent):void{
    trace("[ERROR:${cmdName}]"+event);
}

${:import(org.seasar.akabana.yui.core.ns.handler,flash.events.MouseEvent)}/**
 * 
 * @param event マウスイベント
 */
handler function ${componentId}_click(event:MouseEvent):void {
	${cursor}
}

${:import(org.seasar.akabana.yui.core.ns.handler)}/**
 * 
 * @param event イベント
 */
handler function ${componentId}_${eventType}(event:${eventClass}):void {
	${cursor}
}

${:import(org.seasar.akabana.yui.service.event.FaultStatus,org.seasar.akabana.yui.service.ns.service,org.seasar.akabana.yui.service.ns.rpc_fault,org.seasar.akabana.yui.service.ns.rpc_result)}

/**
 * 
 * @param ${argName} 
 */
service function ${serviceName}$$${serviceMethodName}(${argName}:${argsType}):void{
    ${serviceName:var(org.seasar.akabana.yui.service.Service)}.${serviceMethodName}(${argName}).setResponder( this );
}

/**
 *
 * @param ${resultName} 
 */
rpc_result function ${serviceName:var(org.seasar.akabana.yui.service.Service)}_${serviceMethodName}(${resultName}:${resultType}):void{
    ${cursor}
}

/**
 *
 * @param fault サービス呼び出しエラー
 */        
rpc_fault function ${serviceName}_${serviceMethodName}(fault:FaultStatus):void{
    trace("[FAULT:${serviceName}.${serviceMethodName}]"+fault);
}
